<?phpnamespace App\Http\Resources\v1\Job;use Illuminate\Http\Resources\Json\ResourceCollection;class CategoryWorkCollection extends ResourceCollection{    /**     * Transform the resource collection into an array.     *     * @param  \Illuminate\Http\Request  $request     * @return     */    public function toArray($request)    {        return  $this->collection->map(function ($item){            return[                'id' => $item->id,                'job' => $item->categry_job_id,                'title' => $item->title,                'text'  => $item->title            ];        });    }    public function with($request)    {        return [          'success' => true        ];    }}