<?phpnamespace App\Http\Resources\v1\Job;use Illuminate\Http\Resources\Json\JsonResource;use App\Http\Resources\v1\Job\JobCategoryCollection;class Job extends JsonResource{    /**     * Transform the resource into an array.     *     * @param  \Illuminate\Http\Request  $request     * @return array     */    public function toArray($request)    {        return [            'id'    => $this->id,            'title' => $this->title,            'image' => $this->image,            'jobCategory' => new JobCategoryCollection($this->CategoryJobs) ,            'text'  => $this->title        ];    }}