<?phpnamespace App\Http\Resources\v1\Job;use Illuminate\Http\Resources\Json\ResourceCollection;use App\Http\Resources\v1\Job\CategoryWorkCollection;class JobCategoryCollection extends ResourceCollection{    /**     * Transform the resource collection into an array.     *     * @param  \Illuminate\Http\Request  $request     * @return     */    public function toArray($request)    {        return  $this->collection->map(function ($item){            return[                'id' => $item->id,                'job' => $item->id_job,                'title' => $item->title,                'text'  => $item->title ,                'work_category'  => new CategoryWorkCollection($item->CategoryWork)            ];        });    }    public function with($request)    {        return [            'success' => true        ];    }}