<?phpnamespace App\Http\Resources\v1\Map;use Illuminate\Http\Resources\Json\JsonResource;class City extends JsonResource{    /**     * Transform the resource into an array.     *     * @param  \Illuminate\Http\Request  $request     * @return array     */    public function toArray($request)    {            return [            'id'            => $this->id,            'title'         => $this->title,            'province_id'   => $this->province_id,            'text'         => $this->title        ];    }}