<?phpnamespace App\Http\Resources\v1\language;use Illuminate\Http\Resources\Json\JsonResource;class language extends JsonResource{    /**     * Transform the resource into an array.     *     * @param  \Illuminate\Http\Request  $request     * @return array     */    public function toArray($request)    {        return [            'id'    => $this->id,            'title' => $this->title,            'text'  => $this->title        ];    }}